import { useState } from 'react';
import CampoNumero from '../CampoNumero';

const Formulario = ({ calcular }) => {
    const [valorX, setValorX] = useState('');
    const [valorY, setValorY] = useState('');

    const validacaoX = (value) => {
        // Permite apenas números não negativos e verifica se não é maior que Y
        if (value === '' || (parseFloat(value) >= 0 && (valorY === '' || parseFloat(value) <= parseFloat(valorY)))) {
            setValorX(value);
        }
    };

    const validacaoY = (value) => {
        // Permite apenas números não negativos e verifica se não é maior que X
        if (value === '' || (parseFloat(value) >= 0 && (valorX === '' || parseFloat(value) <= parseFloat(valorX)))) {
            setValorY(value);
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        calcular(valorX, valorY); // Chama a função de cálculo aqui
    };

    return (
        <section className="formulario">
            <form onSubmit={handleSubmit}>
                <h2>Verificador de Menor Múltiplo Comum</h2>
                <CampoNumero
                    label='Número x:'
                    value={valorX}
                    onChange={(e) => validacaoX(e.target.value)} // Chama a validação
                />
                <CampoNumero
                    label='Número y:'
                    value={valorY}
                    onChange={(e) => validacaoY(e.target.value)} // Chama a validação
                />
                <button type="submit">Calcular</button>
            </form>
        </section>
    );
};

export default Formulario;
