import { useState } from 'react';
import CampoNumero from '../CampoNumero';

const Formulario = ({calcular}) => {
    const [valorX, setValorX] = useState('');
    const [valorY, setValorY] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        if (calcular) {
            calcular(Number(valorX), Number(valorY)); // Chama a função com os valores de entrada
        }
    };

    return (
        <section className="formulario">
            <form onSubmit={handleSubmit}>
                <h2>Verificador de Menor Múltiplo Comum</h2>
                <CampoNumero label='Numero x:'
                    value={valorX}
                    onChange={(e) => setValorX(e.target.value)}/>
                <CampoNumero label='Numero y:'
                    value={valorY}
                    onChange={(e) => setValorY(e.target.value)}/>
                <button type="submit">Calcular</button>
            </form>
        </section>
    )
}

export default Formulario;
