import { useState } from 'react';
import Banner from './componentes/Banner';
import Formulario from './componentes/Formulario';
import ResultadoDivisao from './componentes/ResultadoDivisao';

function App() {
  const [resultado, setResultado] = useState(null);
  const [valorX, setValorX] = useState('');
  const [valorY, setValorY] = useState('');

  // Função que simula o cálculo ou a chamada à API Django
  const calcular = (x, y) => {
      console.log(`Calculando com X: ${x} e Y: ${y}`);

      const resultadoCalculado = ''
      setResultado(resultadoCalculado);
  };

  return (
    <div className="App">
      <Banner />
      <Formulario calcular={calcular} />
      <ResultadoDivisao resultado={resultado} x={valorX} y={valorY} />
    </div>
  );
}

export default App;
